package while_;

public class Ex01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
/*
while(조건식){
	종속문장
}
다음문장
*/
		int i = 1, sum=0;
		
		while(i<=10) {
			sum+=i;
			i++;
		}System.out.println("while : "+sum);
		
		
		for(i=1,sum=0;i<=10;i++) {
			sum+=i;
		}System.out.println("for : "+sum);
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

} 


